import React from "react";
import { useState, useEffect } from "react";
import Dashboard from "../dashboard/dashboard";
import { Table } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { Form, Row, Col } from "react-bootstrap";
import Swal from "sweetalert2";
import axios from "axios";
// import Modal from "react-bootstrap/ModalHeader";
import "bootstrap/dist/css/bootstrap.min.css";

function AddModal(props) {
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [age, seAge] = useState("");
  // console.log("fname", fname);
  function createPost() {
    axios
      .post(`http://localhost:3001/create`, {
        first_name: fname,
        last_name: lname,
        age: age,
      })
      .then((response) => {});
  }

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Add Employee
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group as={Row} className="mb-3" controlId="formHorizontalEmail">
            <Form.Label column sm={2}>
              First Name
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                placeholder="First Name"
                onChange={(e) => setFname(e.target.value)}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} className="mb-3">
            <Form.Label column sm={2}>
              Last Name
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                placeholder="Last Name"
                onChange={(e) => setLname(e.target.value)}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} className="mb-3">
            <Form.Label column sm={2}>
              Age
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                placeholder="Age"
                onChange={(e) => seAge(e.target.value)}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} className="mb-3">
            <Col sm={{ span: 10, offset: 2 }}>
              <Button type="submit" onClick={createPost}>
                ADD
              </Button>
            </Col>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

function UpdateModal(props) {
  const [items, setItems] = useState({});
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [age, setAge] = useState("");
  console.log("ewwwww", props.id);

  useEffect(async () => {
    const getData = async () => {
      axios.get(`http://localhost:3001/employee/${props.id}`).then((res) => {
        setItems(res.data);
        setFname(res.data.first_name);
        setLname(res.data.last_name);
        setAge(res.data.age);
        console.log("data", res.data);
      });
    };
    getData();
  }, [fname]);

  function updatePost() {
    axios
      .put(`http://localhost:3001/employee/6207513d2fe9f1c86bc2dc6d`, {
        first_name: fname,
        last_name: lname,
        age: age,
      })
      .then((response) => {});
  }

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Add Employee
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group as={Row} className="mb-3" controlId="formHorizontalEmail">
            <Form.Label column sm={2}>
              First Name
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                onChange={(e) => setFname(e.target.value)}
                value={fname}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} className="mb-3">
            <Form.Label column sm={2}>
              Last Name
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                value={lname}
                onChange={(e) => setLname(e.target.value)}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} className="mb-3">
            <Form.Label column sm={2}>
              Age
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                value={age}
                onChange={(e) => setAge(e.target.value)}
              />
            </Col>
          </Form.Group>

