import React from "react";
import { useState, useEffect } from "react";
import Dashboard from "../dashboard/dashboard";
import { Table } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { Form, Row, Col } from "react-bootstrap";
import Swal from "sweetalert2";
import axios from "axios";
// import Modal from "react-bootstrap/ModalHeader";
import "bootstrap/dist/css/bootstrap.min.css";

function MyVerticallyCenteredModal(props) {
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [age, seAge] = useState("");
  console.log("fname", fname);
  function createPost() {
    axios
      .post(`http://localhost:3001/create`, {
        first_name:fname ,
        last_name:lname ,
        age:age
      })
      .then((response) => {
      });
  }

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Add Employee
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group as={Row} className="mb-3" controlId="formHorizontalEmail">
            <Form.Label column sm={2}>
              First Name
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                placeholder="First Name"
                onChange={(e) => setFname(e.target.value)}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} className="mb-3">
            <Form.Label column sm={2}>
              Last Name
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                placeholder="Last Name"
                onChange={(e) => setLname(e.target.value)}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} className="mb-3">
            <Form.Label column sm={2}>
              Age
            </Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                placeholder="Age"
                onChange={(e) => seAge(e.target.value)}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} className="mb-3">
            <Col sm={{ span: 10, offset: 2 }}>
              <Button type="submit" onClick={createPost}>ADD</Button>
            </Col>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

function EmployeesList() {
  const [modalShow, setModalShow] = React.useState(false);
  document.title = "Employees";
  const [items, setItems] = useState([]);
  {console.log("itemID".items)}
  const getData = async () => {
    axios.get(`http://localhost:3001/get_all`).then((res) => {
      setItems(res.data);
      console.log("data", res);
    });
  };
  async function deleteOperation(id) {
    axios
      .delete(`http://localhost:3001/employee/${id}`)
      .then(() => {
        alert("Post deleted!");
      });
    getData();
    Swal.fire("Good job!", "Employee Deleted Successfully", "success");
  }


  useEffect(() => {
    getData();
  }, []);

  return (
    <div>
      <Dashboard />

      <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
      <div className="table-wrapper" style={{ margin: "5% 25% 0 20%" }}>
        <h2>Employees</h2>
        <Button
          onClick={() => setModalShow(true)}
          variant="primary"
          size="lg"
          block="block"
          type="submit"
          style={{ margin: "2% 0 4% 0" }}
        >
          Add Employee
        </Button>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Age</th>
              <th style={{ width: "50px" }}>Delete</th>
              <th style={{ width: "50px" }}>Edit</th>
            </tr>
          </thead>
          <tbody>
            {items.map((item) => (
              <tr>
                <td>{item.first_name}</td>
                <td>{item.last_name}</td>
                <td>{item.age}</td>

                <td>
                  <Button size="sm" variant="danger"  onClick={() => deleteOperation(item.id)}>
                    Delete
                  </Button>
                </td>
                <td>
                  {" "}
                  <Button
                    size="sm"
                    variant="info"
                    style={{ marginRight: "10px" }}
                  >
                    Edit
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    </div>
  );
}
export default EmployeesList;
